/**
 * Rearranges an array of numbers such that even numbers are placed at even indices
 * and odd numbers are placed at odd indices. If the counts of even and odd numbers
 * do not match the required counts for such a rearrangement, returns an empty array.
 * 
 * @param nums - The array of numbers to be rearranged.
 * @returns The rearranged array or an empty array if rearrangement is not possible.
 */
function rearrangeArray(nums: number[]): number[] {
    let evenCount = 0;
    let oddCount = 0;
    const N = nums.length;
    const result: number[] = new Array(N).fill(0);

    // Count the number of even and odd numbers in the array
    for (const num of nums) {
        if (num % 2 === 0) {
            evenCount++;
        } else {
            oddCount++;
        }
    }

    // Check if the counts of even and odd numbers allow for the rearrangement
    if (evenCount !== Math.floor((N + 1) / 2) || oddCount !== Math.floor(N / 2)) {
        return [];
    }

    // Rearrange the numbers
    let evenIdx = 0;
    let oddIdx = 1;
    for (const num of nums) {
        if (num % 2 === 0) {
            result[evenIdx] = num;
            evenIdx += 2;
        } else {
            result[oddIdx] = num;
            oddIdx += 2;
        }
    }

    return result;
}

