/**
 * Returns the smallest number of passes needed to sort the array using Bubble Sort.
 * @param arr - The array to be sorted.
 * @returns The number of passes required to sort the array.
 */
function bubbleSortPasses(arr: number[]): number {
    const n = arr.length;
    let numPasses = 0;
    let swapped: boolean;

    do {
        swapped = false;
        for (let i = 1; i < n; i++) {
            if (arr[i - 1] > arr[i]) {
                // Swap elements
                [arr[i - 1], arr[i]] = [arr[i], arr[i - 1]];
                swapped = true;
            }
        }
        numPasses++;
    } while (swapped);

    return numPasses;
}

