/**
 * Sorts the list of employee tuples by their last name, then by first name, and then by ID.
 * Each employee tuple is of the form [id, firstName, lastName].
 */
function sortEmployees(data: [string, string, string][]): [string, string, string][] {
    return data.sort((a, b) => {
        // Compare last names first
        if (a[2] < b[2]) return -1;
        if (a[2] > b[2]) return 1;

        // If last names are equal, compare first names
        if (a[1] < b[1]) return -1;
        if (a[1] > b[1]) return 1;

        // If first names are also equal, compare IDs
        if (a[0] < b[0]) return -1;
        if (a[0] > b[0]) return 1;

        return 0;
    });
}

