/**
 * Simulates traversing a grid to collect maximum values while moving to adjacent cells.
 * Starts at (0, 0) and always moves to the adjacent cell with the highest value until reaching the bottom-right corner.
 * @param grid - 2D array of numbers representing cell values
 * @returns The total sum collected during traversal
 */
function traverseGridAndSum(grid: number[][]): number {
    const R = grid.length;
    if (R === 0) return 0;
    const C = grid[0].length;

    let currRow = 0;
    let currCol = 0;
    let sumTotal = 0;

    while (true) {
        // Add current cell value to total
        sumTotal += grid[currRow][currCol];

        // Check if we've reached the end
        if (currRow === R - 1 && currCol === C - 1) {
            break;
        }

        // Mark current cell as visited by setting to 0
        grid[currRow][currCol] = 0;

        let maxVal = -1;
        let nextRow = -1;
        let nextCol = -1;

        // Possible movement directions: up, down, left, right
        const directions = [[-1, 0], [1, 0], [0, -1], [0, 1]];

        // Find the adjacent cell with maximum value
        for (const [dr, dc] of directions) {
            const nr = currRow + dr;
            const nc = currCol + dc;

            // Check if new position is within grid bounds
            if (nr >= 0 && nr < R && nc >= 0 && nc < C) {
                const val = grid[nr][nc];
                if (val > maxVal) {
                    maxVal = val;
                    nextRow = nr;
                    nextCol = nc;
                }
            }
        }

        // Move to the selected cell
        currRow = nextRow;
        currCol = nextCol;
    }

    return sumTotal;
}

