/**
 * Calculates the maximum coverage area that can be achieved by gliding over segments
 * with a given maximum non-gliding distance (h).
 * 
 * @param h The maximum non-gliding distance allowed
 * @param segments Array of tuples representing the segments [start, end]
 * @returns The maximum coverage area
 */
function calculateMaxGlidingCoverage(h: number, segments: [number, number][]): number {
    const n = segments.length;

    // Calculate gaps between consecutive segments
    const gaps: number[] = [];
    for (let i = 0; i < n - 1; i++) {
        gaps.push(segments[i + 1][0] - segments[i][1]);
    }

    let left = 0;
    let currentSum = 0;
    let maxVal = 0;

    for (let right = 0; right < n; right++) {
        if (right > 0) {
            currentSum += gaps[right - 1];
        }

        // Adjust the left pointer to ensure sum <= h
        while (currentSum > h && left < right) {
            currentSum -= gaps[left];
            left++;
        }

        // Calculate the current window's value
        const xR2 = segments[right][1];
        const xL1 = segments[left][0];
        const currentValue = (xR2 - xL1) + (h - currentSum);

        if (currentValue > maxVal) {
            maxVal = currentValue;
        }
    }

    return maxVal;
}

