    // Test case 1: Basic test case with small gaps
    assert.strictEqual(
        calculateMaxGlidingCoverage(5, [[1, 3], [5, 8], [10, 15], [16, 20]]),
        19
    );

    // Test case 2: Larger gaps between segments
    assert.strictEqual(
        calculateMaxGlidingCoverage(10, [[0, 2], [15, 20], [25, 30], [35, 40]]),
        25
    );

    // Test case 3: Small gaps with small h value
    assert.strictEqual(
        calculateMaxGlidingCoverage(3, [[1, 2], [3, 4], [5, 6], [7, 8]]),
        7
    );

    // Test case 4: Large h value that covers all gaps
    assert.strictEqual(
        calculateMaxGlidingCoverage(100, [[10, 20], [30, 40], [50, 60]]),
        130
    );
}

testCalculateMaxGlidingCoverage();

