/**
 * Parses a string representing a star system and returns an object where the keys are the names 
 * of the celestial bodies and the values are their corresponding distances from the star.
 * 
 * The input string is expected to be in the format "Body1:Distance1, Body2:Distance2, ..."
 * If the input string is empty, returns an empty object.
 */
function parseStarSystem(starSystemString: string): Record<string, number> {
    const result: Record<string, number> = {};

    if (starSystemString.trim() === "") {
        return result;
    }

    const starSystemList = starSystemString.split(", ");
    for (const starSystem of starSystemList) {
        const [body, distance] = starSystem.split(":");
        result[body.trim()] = parseFloat(distance.trim());
    }

    return result;
}

