/**
 * Determines if it's possible to reach the end of a matrix from the start (0,0) to (n-1, n-1)
 * using only right and down movements, where 1 represents a passable cell and 0 represents a blocked cell.
 * @param matrix - A 2D array of numbers where 1 is passable and 0 is blocked.
 * @returns "YES" if the end is reachable, "NO" otherwise.
 */
function canReachEnd(matrix: number[][]): string {
    const n = matrix.length;
    if (matrix[0][0] === 0 || matrix[n-1][n-1] === 0) {
        return "NO";
    }

    // A queue for BFS
    const queue: [number, number][] = [[0, 0]];
    const visited = new Set<string>();
    visited.add("0,0");

    const directions: [number, number][] = [[0, 1], [1, 0]];  // right and down movements

    while (queue.length > 0) {
        const [r, c] = queue.shift()!;

        if (r === n-1 && c === n-1) {
            return "YES";
        }

        for (const [dr, dc] of directions) {
            const nr = r + dr;
            const nc = c + dc;
            const key = `${nr},${nc}`;
            if (0 <= nr && nr < n && 0 <= nc && nc < n && matrix[nr][nc] === 1 && !visited.has(key)) {
                queue.push([nr, nc]);
                visited.add(key);
            }
        }
    }

    return "NO";
}

