/**
 * Answers multiple substring queries on a given list of log strings.
 * For each query, it returns the 1-based index of the first log that contains the query substring,
 * or "No Match" if not found.
 * 
 * @param logs - The list of log strings to search through
 * @param queries - The list of substring queries to search for
 * @returns An array of results for each query
 */
function logQuerySystem(logs: string[], queries: string[]): string[] {
    const results: string[] = [];

    for (const query of queries) {
        let found = false;

        for (let i = 0; i < logs.length; i++) {
            if (logs[i].includes(query)) {
                results.push((i + 1).toString());
                found = true;
                break;
            }
        }

        if (!found) {
            results.push("No Match");
        }
    }

    return results;
}

