/**
 * Implements a circular queue with a fixed capacity.
 * Supports enqueue, dequeue, peek_nth, and size operations.
 */
class CircularQueue {
    private queue: (number | null)[];
    private head: number;
    private tail: number;
    private capacity: number;

    constructor(k: number) {
        this.queue = new Array(k).fill(null);
        this.head = -1;
        this.tail = -1;
        this.capacity = k;
    }

    /**
