/**
 * Returns the minimum number of coins needed to achieve the target sum T,
 * or -1 if it is not possible to achieve T with the given coins.
 * 
 * @param n The number of coin denominations (unused in the function, but kept for interface consistency)
 * @param T The target sum to achieve
 * @param coins Array of coin denominations
 * @returns The minimum number of coins needed or -1 if not possible
 */
function minCoinsToTarget(n: number, T: number, coins: number[]): number {
    const maxValue = T + 1;
    const dp: number[] = new Array(T + 1).fill(maxValue);
    dp[0] = 0;

    for (let i = 1; i <= T; i++) {
        for (const coin of coins) {
            if (i - coin >= 0) {
                dp[i] = Math.min(dp[i], dp[i - coin] + 1);
            }
        }
    }

    return dp[T] !== maxValue ? dp[T] : -1;
}

