/**
 * Calculates the length of the cycle each element belongs to in a permutation array.
 * The function uses a 1-based index for the input array.
 * @param n The number of elements in the array
 * @param A The permutation array (1-based index)
 * @returns An array where each element represents the length of the cycle it belongs to
 */
function calculateCycleLengths(n: number, A: number[]): number[] {
    // Convert to 1-based index by adding a dummy element at index 0
    const arr = [0, ...A];
    const res: number[] = new Array(n + 1).fill(0);
    const visited: boolean[] = new Array(n + 1).fill(false);

    for (let i = 1; i <= n; i++) {
        if (!visited[i]) {
            const cycle: number[] = [];
            let current = i;

            // Traverse the cycle until we find a visited node
            while (true) {
                if (visited[current]) break;
                visited[current] = true;
                cycle.push(current);
                current = arr[current];
            }

            // All elements in this cycle have the same cycle length
            const cycleLength = cycle.length;
            for (const num of cycle) {
                res[num] = cycleLength;
            }
        }
    }

    // Return only the results for indices 1..n (excluding the 0 index)
    return res.slice(1);
}

