    // Test case 1: Two cycles: (1231) and (454)
    assert.deepStrictEqual(
        calculateCycleLengths(5, [2, 3, 1, 5, 4]),
        [3, 3, 3, 2, 2]
    );

    // Test case 2: Two 2-element cycles: (12) and (34)
    assert.deepStrictEqual(
        calculateCycleLengths(4, [2, 1, 4, 3]),
        [2, 2, 2, 2]
    );

    // Test case 3: One big cycle: (1234561)
    assert.deepStrictEqual(
        calculateCycleLengths(6, [2, 3, 4, 5, 6, 1]),
        [6, 6, 6, 6, 6, 6]
    );

    // Test case 4: All elements are fixed points (cycle length 1)
    assert.deepStrictEqual(
        calculateCycleLengths(3, [1, 2, 3]),
        [1, 1, 1]
    );
}

testCalculateCycleLengths();

