/**
 * Calculates the total number of square submatrices with all non-hole cells in a grid.
 * @param H - Number of rows in the grid
 * @param W - Number of columns in the grid
 * @param holes - Array of hole positions as [row, column] pairs (1-based indices)
 * @returns The total count of valid square submatrices
 */
function countSquareSubmatrices(H: number, W: number, holes: [number, number][]): number {
    // Initialize hole grid with 1-based indices (extra row and column for padding)
    const holeGrid: boolean[][] = Array.from({ length: H + 2 }, () => Array(W + 2).fill(false));

    // Mark hole positions
    for (const [a, b] of holes) {
        holeGrid[a][b] = true;
    }

    // Initialize DP table
    const dp: number[][] = Array.from({ length: H + 2 }, () => Array(W + 2).fill(0));
    let total = 0;

    // Fill DP table
    for (let i = 1; i <= H; i++) {
        for (let j = 1; j <= W; j++) {
            if (holeGrid[i][j]) {
                dp[i][j] = 0;
            } else {
                dp[i][j] = Math.min(dp[i-1][j], dp[i][j-1], dp[i-1][j-1]) + 1;
                total += dp[i][j];
            }
        }
    }

    return total;
}

