    assert.strictEqual(countSquareSubmatrices(3, 3, [[2, 2]]), 8);

    // Test case 2: 2x2 grid with no holes
    assert.strictEqual(countSquareSubmatrices(2, 2, []), 5);

    // Test case 3: 4x4 grid with corner holes
    assert.strictEqual(countSquareSubmatrices(4, 4, [[1, 1], [1, 4], [4, 1], [4, 4]]), 17);

    // Test case 4: 1x1 grid with single hole
    assert.strictEqual(countSquareSubmatrices(1, 1, [[1, 1]]), 0);
}

