/**
 * Given a sentence, returns an object with word lengths as keys and arrays 
 * of words of that length as values.
 * If the input sentence is empty, returns an empty object.
 */
function findWords(sentence: string): Record<number, string[]> {
    if (!sentence) {
        return {};
    }

    const words = sentence.split(' ');
    const wordDict: Record<number, string[]> = {};

    for (const word of words) {
        const length = word.length;
        if (wordDict[length]) {
            wordDict[length].push(word);
        } else {
            wordDict[length] = [word];
        }
    }

    return wordDict;
}

