/**
 * Calculates the sum of absolute differences between all pairs of elements in a sequence.
 * The sequence is generated based on given parameters K, C, n, and a0.
 * The sequence generation follows the formula: a[i] = (K * a[i-1] + C) % MOD
 */
function calculateTotalDifference(K: number, C: number, n: number, a0: number): number {
    const MOD = 1000007;
    const a: number[] = new Array(n);
    a[0] = a0;

    // Generate the sequence
    for (let i = 1; i < n; i++) {
        a[i] = (K * a[i-1] + C) % MOD;
    }

    // Sort the sequence
    a.sort((x, y) => x - y);

    // Calculate prefix sums
    const prefix: number[] = new Array(n + 1).fill(0);
    for (let i = 0; i < n; i++) {
        prefix[i + 1] = prefix[i] + a[i];
    }

    // Calculate the total difference
    let total = 0;
    for (let i = 0; i < n; i++) {
        total += a[i] * i - prefix[i];
    }

    return total;
}

