/**
 * Simulates the movement of a ball under cups based on a sequence of swaps.
 * The cups are initially in positions 1 (left), 2 (middle), 3 (right).
 * The ball starts under cup 1.
 * 
 * @param swapSequence - A string containing the swap commands (A, B, or C)
 * @returns The final position of the ball (1, 2, or 3)
 */
function simulateCupSwaps(swapSequence: string): number {
    let pos = 1;  // Initial position is leftmost cup (1)

    for (const c of swapSequence) {
        switch (c) {
            case 'A':
                if (pos === 1) pos = 2;
                else if (pos === 2) pos = 1;
                break;
            case 'B':
                if (pos === 2) pos = 3;
                else if (pos === 3) pos = 2;
                break;
            case 'C':
                if (pos === 1) pos = 3;
                else if (pos === 3) pos = 1;
                break;
            // Note: We ignore any other characters in the sequence
        }
    }

    return pos;
}

