/**
 * Splits a balanced bracket string into two parts at the point where the brackets first balance.
 * @param content - A string containing balanced square brackets
 * @returns A tuple of two strings representing the left and right parts after splitting
 */
function splitContent(content: string): [string, string] {
    let balance = 0;
    let splitIndex = 0;
    for (let i = 0; i < content.length; i++) {
        const c = content[i];
        if (c === '[') {
            balance += 1;
        } else {
            balance -= 1;
        }
        if (balance === 0) {
            splitIndex = i + 1;
            break;
        }
    }
    return [content.substring(0, splitIndex), content.substring(splitIndex)];
}

/**
 * Computes the minimum number of monkeys needed to balance a given bracket string.
 * @param s - A string containing balanced square brackets (or empty)
 * @returns The minimum number of monkeys required
 */
function computeMinMonkeys(s: string): number {
    if (!s) {
        return 1;
    }
    const content = s.substring(1, s.length - 1);
    if (!content) {
        return 2;
    }
    const [leftPart, rightPart] = splitContent(content);
    const left = computeMinMonkeys(leftPart);
    const right = computeMinMonkeys(rightPart);
    return 2 * Math.max(left, right);
}

