    assert.strictEqual(computeMinMonkeys(""), 1);
    assert.strictEqual(computeMinMonkeys("[]"), 2);
    assert.strictEqual(computeMinMonkeys("[[]]"), 4);
    assert.strictEqual(computeMinMonkeys("[[][]]"), 4);
    assert.strictEqual(computeMinMonkeys("[[][][]]"), 8);
    assert.strictEqual(computeMinMonkeys("[[[]][]]"), 8);
    assert.strictEqual(computeMinMonkeys("[[[[][]]][[][]]]"), 16);
}

testComputeMinMonkeys();

