/**
 * Determines if there exists a contiguous subarray of length at least 2 that sums up to a multiple of k.
 * @param arr - The input array of numbers.
 * @param k - The number to check if the subarray sum is a multiple of.
 * @returns True if such a subarray exists, otherwise False.
 */
function checkSubarraySum(arr: number[], k: number): boolean {
    if (!arr || arr.length < 2) {
        return false;
    }

    let prefixSum = 0;
    const prefixSumMod: Record<number, number> = { 0: -1 }; // to handle the case when the subarray starts from index 0

    for (let i = 0; i < arr.length; i++) {
        prefixSum += arr[i];
        const mod = k !== 0 ? prefixSum % k : prefixSum;

        if (mod in prefixSumMod) {
            if (i - prefixSumMod[mod] > 1) {
                return true;
            }
        } else {
            prefixSumMod[mod] = i;
        }
    }

    return false;
}

