/**
 * Given a square matrix, calculates the sum of the primary and secondary diagonal elements.
 * If the matrix size is odd, the center element is only summed once.
 */
function sumOfDiagonals(matrix: number[][]): number {
    const n = matrix.length;
    let sumDiag = 0;

    for (let i = 0; i < n; i++) {
        sumDiag += matrix[i][i];  // primary diagonal
        sumDiag += matrix[i][n - 1 - i];  // secondary diagonal
    }

    if (n % 2 === 1) {
        const center = matrix[Math.floor(n / 2)][Math.floor(n / 2)];
        sumDiag -= center;  // subtract the center value as it is counted twice
    }

    return sumDiag;
}

assertions:
