/**
 * Returns the maximum score of any subarray of length k using integer division.
 * If there is no such subarray, return -1.
 * 
 * @param nums - The array of integers
 * @param k - The length of the subarray
 * @returns The maximum score or -1 if no such subarray exists
 */
function maxSubarrayScore(nums: number[], k: number): number {
    const n = nums.length;
    if (n < k) {
        return -1;
    }

    let maxScore = -1;
    for (let i = 0; i <= n - k; i++) { // Loop through each possible starting point of subarray of length k
        const subarray = nums.slice(i, i + k);
        const sum = subarray.reduce((acc, val) => acc + val, 0);
        const score = Math.floor(sum / k); // Integer division to get the score
        if (score > maxScore) {
            maxScore = score;
        }
    }

    return maxScore;
}

