/**
 * Represents an editor operation (insert or delete)
 */
interface Operation {
    action: 'insert' | 'delete';
    char: string;
}

/**
 * Simple text editor with undo/redo functionality
 */
class SimpleEditor {
    private text: string[];
    private undoStack: Operation[];
    private redoStack: Operation[];

    constructor() {
        this.text = [];
        this.undoStack = [];
        this.redoStack = [];
    }

    /**
     * Insert a character at the end of the text
     * @param char Character to insert
     */
    insert(char: string): void {
        this.text.push(char);
        this.undoStack.push({ action: 'insert', char });
        this.redoStack = [];
    }

    /**
     * Delete the last character from the text
     */
    delete(): void {
        if (this.text.length > 0) {
            const char = this.text.pop()!;
            this.undoStack.push({ action: 'delete', char });
            this.redoStack = [];
        }
    }

    /**
     * Undo the last operation
     */
    undo(): void {
        if (this.undoStack.length > 0) {
            const operation = this.undoStack.pop()!;
            if (operation.action === 'insert') {
                this.text.pop();
            } else {
                this.text.push(operation.char);
            }
            this.redoStack.push(operation);
        }
    }

    /**
     * Redo the last undone operation
     */
    redo(): void {
        if (this.redoStack.length > 0) {
            const operation = this.redoStack.pop()!;
            if (operation.action === 'insert') {
                this.text.push(operation.char);
            } else {
                this.text.pop();
            }
            this.undoStack.push(operation);
        }
    }

    /**
     * Get the current text as a string
     * @returns The current text content
     */
    getText(): string {
        return this.text.join('');
    }
}

assertions:
const assert = require('assert');

function testSimpleEditor() {
    // Test 1: Basic insert and delete
    const editor1 = new SimpleEditor();
    editor1.insert('a');
    editor1.insert('b');
