    assert.strictEqual(editor1.getText(), 'abc');
    editor1.delete();
    assert.strictEqual(editor1.getText(), 'ab');
    editor1.undo();
    assert.strictEqual(editor1.getText(), 'abc');
    editor1.redo();
    assert.strictEqual(editor1.getText(), 'ab');

    // Test 2: Multiple undos/redos
