    editor2.insert('a');
    editor2.insert('b');
    editor2.insert('d');
    editor2.insert('e');
    assert.strictEqual(editor2.getText(), 'abde');
    editor2.undo();
    editor2.undo();
    assert.strictEqual(editor2.getText(), 'ab');
    editor2.redo();
    editor2.redo();
    assert.strictEqual(editor2.getText(), 'abde');

    // Test 3: Edge cases
    const emptyEditor = new SimpleEditor();
    assert.strictEqual(emptyEditor.getText(), '');
    emptyEditor.delete(); // Should do nothing
    assert.strictEqual(emptyEditor.getText(), '');
    emptyEditor.undo(); // Should do nothing
    assert.strictEqual(emptyEditor.getText(), '');
    emptyEditor.redo(); // Should do nothing
    assert.strictEqual(emptyEditor.getText(), '');
