/**
 * Counts the number of subarrays with at most K distinct elements.
 * Helper function for subarraysWithKDistinct.
 */
function atMostKDistinct(nums: number[], k: number): number {
    const countMap: Record<number, number> = {};
    let left = 0;
    let result = 0;
    let distinctCount = 0;

    for (let right = 0; right < nums.length; right++) {
        const num = nums[right];
        if (!countMap[num] || countMap[num] === 0) {
            distinctCount++;
        }
        countMap[num] = (countMap[num] || 0) + 1;

        while (distinctCount > k) {
            const leftNum = nums[left];
            countMap[leftNum]--;
            if (countMap[leftNum] === 0) {
                distinctCount--;
            }
            left++;
        }
        result += right - left + 1;
    }

    return result;
}

/**
 * Counts the number of subarrays with exactly K distinct elements.
 * Uses the sliding window technique with the helper function atMostKDistinct.
 */
function subarraysWithKDistinct(nums: number[], k: number): number {
    return atMostKDistinct(nums, k) - atMostKDistinct(nums, k - 1);
}
const assert = require('assert');

function testSubarraysWithKDistinct() {
    assert.strictEqual(subarraysWithKDistinct([1, 2, 1, 2, 3], 2), 7);
    assert.strictEqual(subarraysWithKDistinct([1, 2, 1, 3, 4], 3), 3);
    assert.strictEqual(subarraysWithKDistinct([1, 1, 1, 1, 1], 1), 15);
    assert.strictEqual(subarraysWithKDistinct([1, 2, 3, 4, 5], 1), 5);
    assert.strictEqual(subarraysWithKDistinct([], 2), 0);
}

testSubarraysWithKDistinct();

