/**
 * Converts a snake_case variable name to CamelCase following the specific style guide rules.
 * The first component remains as-is, and subsequent components are title-cased and concatenated.
 * @param snakeStr The snake_case string to convert.
 * @returns The converted CamelCase string.
 */
function snakeToCamel(snakeStr: string): string {
    const components = snakeStr.split('_');
    return components[0] + components.slice(1).map(x => x.charAt(0).toUpperCase() + x.slice(1)).join('');
}

/**
 * Converts an array of snake_case variable names to CamelCase.
 * @param n The number of variable names (unused in this function but kept for interface consistency).
 * @param variableNames The array of snake_case strings to convert.
 * @returns The array of converted CamelCase strings.
 */
function convertSnakeToCamel(n: number, variableNames: string[]): string[] {
    return variableNames.map(snakeStr => snakeToCamel(snakeStr));
}

