/**
 * Computes the greatest common divisor (GCD) of two numbers using the Euclidean algorithm.
 * @param a - First number
 * @param b - Second number
 * @returns The GCD of a and b
 */
function gcd(a: number, b: number): number {
    while (b !== 0) {
        const temp = b;
        b = a % b;
        a = temp;
    }
    return a;
}

/**
 * Finds all coprimes with M in the range [1, M].
 * @param M - The upper bound for the range and the number to check coprimes against
 * @returns An array of numbers coprime with M
 */
function findCoprimes(M: number): number[] {
    const coprimes: number[] = [];
    for (let k = 1; k <= M; k++) {
        if (gcd(k, M) === 1) {
            coprimes.push(k);
        }
    }
    return coprimes;
}

/**
 * Processes each query to compute the result based on the given parameters.
 * @param a_list - List of integers to process
 * @param M - The number to check coprimes against
 * @param coprimes - List of coprimes with M in [1, M]
 * @returns List of results as strings
 */
function processQueries(a_list: number[], M: number, coprimes: number[]): string[] {
    const phi = coprimes.length;
    const result: string[] = [];
    for (const a of a_list) {
        const q = Math.floor((a - 1) / phi);
        const r = (a - 1) % phi;
        const res = q * M + coprimes[r];
        result.push(res.toString());
    }
    return result;
}

