/**
 * A custom implementation of a hash map in TypeScript.
 * This class provides basic hash map operations such as put, get, and remove.
 * It also handles resizing when the load factor exceeds a threshold.
 */
class CustomHashMap {
    private capacity: number;
    private size: number;
    private loadFactorThreshold: number;
    private buckets: [number, number][][];

    /**
     * Initializes the CustomHashMap with a given initial capacity.
     * @param initialCapacity The initial capacity of the hash map (default: 100).
     */
    constructor(initialCapacity: number = 100) {
        this.capacity = initialCapacity;
        this.size = 0;
        this.loadFactorThreshold = 0.7;
        this.buckets = new Array(this.capacity).fill(null).map(() => []);
    }

    /**
     * Computes the hash for a given key.
     * @param key The key to hash.
     * @returns The computed hash value.
     */
    private hash(key: number): number {
        return key % this.capacity;
    }

    /**
     * Resizes the hash map when the load factor exceeds the threshold.
     * Doubles the capacity and rehashes all existing key-value pairs.
     */
    private resize(): void {
        const newCapacity = this.capacity * 2;
        const newBuckets: [number, number][][] = new Array(newCapacity).fill(null).map(() => []);

        for (const bucket of this.buckets) {
            for (const [key, value] of bucket) {
                const newHash = key % newCapacity;
                newBuckets[newHash].push([key, value]);
            }
        }

        this.buckets = newBuckets;
        this.capacity = newCapacity;
    }

    /**
     * Inserts or updates a key-value pair in the hash map.
     * @param key The key to insert or update.
     * @param value The value to associate with the key.
     */
    put(key: number, value: number): void {
        if (this.size / this.capacity > this.loadFactorThreshold) {
            this.resize();
        }

        const hashKey = this.hash(key);
        const bucket = this.buckets[hashKey];

        for (let i = 0; i < bucket.length; i++) {
            if (bucket[i][0] === key) {
                bucket[i][1] = value; // Update existing key
                return;
            }
        }

        bucket.push([key, value]); // Insert new key-value pair
        this.size++;
    }

    /**
     * Retrieves the value associated with a key.
     * @param key The key to look up.
     * @returns The associated value, or -1 if the key is not found.
     */
    get(key: number): number {
        const hashKey = this.hash(key);
        const bucket = this.buckets[hashKey];

        for (const [k, v] of bucket) {
            if (k === key) {
                return v;
            }
        }

        return -1;
    }

    /**
     * Removes a key-value pair from the hash map.
     * @param key The key to remove.
     */
    remove(key: number): void {
        const hashKey = this.hash(key);
        const bucket = this.buckets[hashKey];

        for (let i = 0; i < bucket.length; i++) {
            if (bucket[i][0] === key) {
                bucket.splice(i, 1);
                this.size--;
                return;
            }
        }
    }
}

