/**
 * Returns the minimum difference between the height of the tallest painted building and the shortest painted building.
 * @param heights - An array of building heights.
 * @param k - The number of buildings to paint.
 * @returns The minimum difference between the tallest and shortest painted building.
 */
function minDifference(heights: number[], k: number): number {
    if (k <= 1) {
        return 0;
    }

    // Sort the array in ascending order
    const sortedHeights = [...heights].sort((a, b) => a - b);
    let minDiff = Infinity;

    // Iterate through the array to find the minimum difference
    for (let i = 0; i <= sortedHeights.length - k; i++) {
        const diff = sortedHeights[i + k - 1] - sortedHeights[i];
        minDiff = Math.min(minDiff, diff);
    }

    return minDiff;
}

